import React, { useState } from "react";
import { Film, Loader2, Star } from "lucide-react";

function App() {
  const [preference, setPreference] = useState("");
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const getRecommendations = async () => {
    if (!preference.trim()) {
      setError("Please enter your movie preference");
      return;
    }

    setLoading(true);
    setError("");
    setMovies([]);

    try {
      const response = await fetch(
        "http://localhost:5001/api/recommendations",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ user_input: preference }),
        }
      );

      const data = await response.json();

      if (data.error) {
        throw new Error(data.error);
      }

      setMovies(data.movies);
    } catch (err) {
      setError(err.message || "Failed to get recommendations");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
        padding: "40px 20px",
      }}
    >
      <div style={{ maxWidth: "800px", margin: "0 auto" }}>
        <div style={{ textAlign: "center", marginBottom: "40px" }}>
          <div
            style={{
              display: "inline-flex",
              alignItems: "center",
              gap: "12px",
              color: "white",
              fontSize: "36px",
              fontWeight: "bold",
              marginBottom: "8px",
            }}
          >
            <Film size={40} />
            Movie Recommender
          </div>
          <p style={{ color: "rgba(255,255,255,0.9)", fontSize: "18px" }}>
            Get personalized movie recommendations powered by AI
          </p>
        </div>

        <div
          style={{
            background: "white",
            borderRadius: "16px",
            padding: "32px",
            boxShadow: "0 20px 60px rgba(0,0,0,0.3)",
          }}
        >
          <div style={{ marginBottom: "24px" }}>
            <label
              style={{
                display: "block",
                marginBottom: "8px",
                fontWeight: "600",
                color: "#333",
              }}
            >
              What kind of movies do you like?
            </label>
            <input
              type="text"
              value={preference}
              onChange={(e) => setPreference(e.target.value)}
              placeholder="e.g., action movies with a strong female lead"
              style={{
                width: "100%",
                padding: "12px",
                border: "2px solid #e5e7eb",
                borderRadius: "8px",
                fontSize: "16px",
                outline: "none",
                transition: "border 0.3s",
              }}
              onFocus={(e) => (e.target.style.borderColor = "#667eea")}
              onBlur={(e) => (e.target.style.borderColor = "#e5e7eb")}
              onKeyPress={(e) => e.key === "Enter" && getRecommendations()}
            />
          </div>

          <button
            onClick={getRecommendations}
            disabled={loading}
            style={{
              width: "100%",
              padding: "14px",
              background: loading
                ? "#9ca3af"
                : "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
              color: "white",
              border: "none",
              borderRadius: "8px",
              fontSize: "16px",
              fontWeight: "600",
              cursor: loading ? "not-allowed" : "pointer",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              gap: "8px",
              transition: "transform 0.2s",
            }}
            onMouseEnter={(e) =>
              !loading && (e.target.style.transform = "scale(1.02)")
            }
            onMouseLeave={(e) => (e.target.style.transform = "scale(1)")}
          >
            {loading ? (
              <>
                <Loader2
                  size={20}
                  style={{ animation: "spin 1s linear infinite" }}
                />
                Getting Recommendations...
              </>
            ) : (
              <>
                <Star size={20} />
                Get Recommendations
              </>
            )}
          </button>

          {error && (
            <div
              style={{
                marginTop: "16px",
                padding: "12px",
                background: "#fee2e2",
                border: "1px solid #fecaca",
                borderRadius: "8px",
                color: "#991b1b",
              }}
            >
              {error}
            </div>
          )}
        </div>

        {movies.length > 0 && (
          <div style={{ marginTop: "32px" }}>
            <h2
              style={{
                color: "white",
                fontSize: "24px",
                fontWeight: "bold",
                marginBottom: "20px",
                textAlign: "center",
              }}
            >
              Your Personalized Recommendations
            </h2>
            <div style={{ display: "grid", gap: "16px" }}>
              {movies.map((movie, index) => (
                <div
                  key={index}
                  style={{
                    background: "white",
                    borderRadius: "12px",
                    padding: "20px",
                    boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
                    transition: "transform 0.2s",
                  }}
                  onMouseEnter={(e) =>
                    (e.currentTarget.style.transform = "translateY(-4px)")
                  }
                  onMouseLeave={(e) =>
                    (e.currentTarget.style.transform = "translateY(0)")
                  }
                >
                  <div
                    style={{
                      display: "flex",
                      justifyContent: "space-between",
                      alignItems: "start",
                      marginBottom: "8px",
                    }}
                  >
                    <h3
                      style={{
                        fontSize: "20px",
                        fontWeight: "bold",
                        color: "#1f2937",
                        margin: 0,
                      }}
                    >
                      {movie.title}
                    </h3>
                    <span
                      style={{
                        background: "#667eea",
                        color: "white",
                        padding: "4px 12px",
                        borderRadius: "20px",
                        fontSize: "14px",
                        fontWeight: "600",
                      }}
                    >
                      {movie.year}
                    </span>
                  </div>
                  <p
                    style={{
                      color: "#6b7280",
                      fontSize: "14px",
                      fontWeight: "600",
                      marginBottom: "8px",
                    }}
                  >
                    {movie.genre}
                  </p>
                  <p
                    style={{
                      color: "#4b5563",
                      fontSize: "15px",
                      lineHeight: "1.6",
                      margin: 0,
                    }}
                  >
                    {movie.description}
                  </p>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      <style>{`
        @keyframes spin {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
}

export default App;
